const googleSheetsData = $input.all().map((item) => item.json);
const telegramData = $("Telegram Trigger")
  .all()
  .map((item) => item.json);

const result = telegramData.map((telegramItem) => {
  const matchedItem = googleSheetsData.find(
    (sheetItem) => sheetItem.ID === telegramItem.message.from.id,
  );

  if (matchedItem) {
    return {
      status: isNaN(matchedItem.Status) ? "match" : matchedItem.Status,
    };
  } else {
    return {
      status: "new",
    };
  }
});

return result;

